import React, { Component } from 'react';
import './App.css';
import Typical from 'react-typical';
import ActCard from './Components/ActeursCard';
import Login from './Components/login_compo/login';
import {BrowserRouter,Route,Switch,Link} from 'react-router-dom';
import Etudiant from './Etudiant/Etudiant';


const acteurs=[
  {type:'Etudiant'},
  {type:'Enseignant'},
  {type:'Administrateur'}
];


const sauv=(<header className="App-header">
<Typical steps={['Bienvenue',1000,'Veuillez choisir votre profil : ',500]}/>
<div className="ActCardsContainer">
  <ActCard  acteur={acteurs[0]} />
  <ActCard  acteur={acteurs[1]} />
  <ActCard  acteur={acteurs[2]} />
</div>
</header>);

class App extends Component {

  render(){
  return (
   <BrowserRouter>
    <div className="App">
  <Link className='welcome' to='/login'> Welcome to Easy School  </Link>
  <Link className='welcome' to='/etudiant'>go to the app  </Link>
    <Route exact path='/Login' > <Login/> </Route>
    <Route exact path='/etudiant'><Etudiant/></Route>
    
    </div>
    </BrowserRouter>
  );};
}

export default App;
