import React, { Component } from 'react';
import './App.css';
import Typical from 'react-typical';
import ActCard from './Components/ActeursCard';

const acteurs=[
  {type:'Etudiant'},
  {type:'Ensiegnant'},
  {type:'Administrateur'}
];

class App extends Component {
  constructor(){
    super(this.props);
    this.routeChangeAdmin=this.routeChangeAdmin.bind(this);
    this.routeChangeEnseignant=this.routeChangeEnseignant.bind(this);
    this.routeChangeEtudiant=this.routeChangeEtudiant.bind(this);

  }
  routeChangeEtudiant(){
    let path= './Etudiant.js';
    this.props.history.push(path);
  }
  routeChangeEnseignant(){
    let path= './Enseignant.js';
    this.props.history.push(path);
  }
  routeChangeAdmin(){
    let path= './Admin.js';
    this.props.history.push(path);
  }
  render(){
  return (
    <div className="App">
      <header className="App-header">
        <Typical steps={['Bienvenue',1000,'Veuillez choisir votre profil : ',500]}/>
        <div className="ActCardsContainer">
          <ActCard onClick={this.routeChangeEtudiant} acteur={acteurs[0]} />
          <ActCard onClick={this.routeChangeEnseignant} acteur={acteurs[1]}/>
          <ActCard onClick={this.routeChangeAdmin} acteur={acteurs[2]}/>
        </div>
      </header>
    </div>
  );};
}

export default App;
